package pack_2;

public class matrix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[][]={
				{1,2,3},
				{4,5,6},
				{7,8,9}
		};
		System.out.println("array values:");
		for(int i=0;i<3;i++)
		{
			for(int j=0;j<3;j++)
			{
				System.out.print(a[i][j]+"");
			}
			System.out.println();
		}

	}

}
